            type: 'SET_USER',
            user: authUser
          })

    }else{
      // user just logged out
      dispatch({
        type: 'SET_USER',
        user:null
      })

    }

   })
  },[])

  return (
  <>
      <div className="App">

        <Routes>
        <Route path="/login" element={<Login/>}/>
        <Route path="/" element={<><Header/><Home/></>}/>

        <Route path="/checkout" element={<> <Header/><Checkout/></>}/>
        <Route path="/payment" element={<> <Header/><Elements stripe={promise}/><Payment/></>}/>
        </Routes>
      </div>
      </>
  );
}

export default App;



